#include "bull.h"#include "tools.h"#include "time.h"#include <sioux.h>#include <profiler.h>using namespace std; //introduces namespace stdvoid SetDefaults(void);long gseed;int main(){		SIOUXSettings.autocloseonquit=true;	SIOUXSettings.asktosaveonclose=true;	#ifdef DZRATES#ifdef CHARBYCHAR	cout<<"Can't do Char by char and DZrates at the same time."<<endl;	return 1;#endif#endif		double d,y;		cout<<setprecision(8);	SIOUXSettings.columns=80;	SIOUXSettings.rows=50;	double **s,a[5];	s=psdmatrix(5);	s[0][0]=s[1][1]=s[2][2]=s[3][3]=1.0;	s[1][4]=s[0][4]=2.0/3.0;	s[2][4]=s[3][4]=-2.0/3;	s[0][1]=s[0][2]=s[0][3]=s[1][0]=s[1][2]=s[1][3]=s[2][0]=s[2][1]=s[2][3]=s[3][0]=s[3][1]=s[3][2]=(-1.0/3);	GaussJordanElimination(s,a,4);	SetDefaults();	Bull bull;	try	{		bull.Run();		}	catch(MTHException x)	{cout<<x.msg<<endl;							char i;							cin>>i;							}	catch(...)				{							cout<<"Exception of undetermined type caught in main loop"<<endl;							}								#ifdef DEBUGGING#endif	return 0;}void SetDefaults(void){	RateManager::set_default(0.1,0.5);	HKY::set_default(2.0,.25,.25,.25);	GTR::set_default(.25,.25,.25,1,2,1,1,2,1);	gseed = 0L;	time(&gseed);	for(int i=0;i<10;i++)		RandomNumber();	}